import React, {useEffect, useState} from 'react'
import "./Login.css"
import Slider from './Slider';
import { TextField } from '@mui/material';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
const Login = () => {
    useEffect(() => {
        document.body.classList.add('login-page');
    }, []);
    const [values, setValues] = React.useState({
        password: '',
        showPassword: false,
      });
      const handleClickShowPassword = () => {
        setValues({
          ...values,
          showPassword: !values.showPassword,
        });
      };
    return (
        <div class="page-wrapper login-page-wrap">
            <div class="login-section">
                <div class="container">
                    <div class="login-wrapper">
                        <div class="login-leftblk">
                            <div class="login-slider">
                                <Slider />
                            </div>
                        </div>
                        <div class="login-rightblk">
                            <div class="login-blk">
                                <div class="logo">
                                    <img src={process.env.PUBLIC_URL + '/images/logo.png'} alt="" class="img-fluid" />
                                </div>
                                <h2 class="heading1 text-uppercase">Log in</h2>
                                <div class="login-form">
                                    <form>
                                        <div class="form-group">
                                            <label for="emailid">Email Id <span class="mandatory">*</span></label>
                                            <TextField id="outlined-basic" placeholder='Enter email id' variant="outlined" />
                                        </div>
                                        <div class="form-group">
                                            <label for="password">Password <span class="mandatory">*</span></label>
                                            <div className='password-field'>
                                                <TextField id="outlined-basic" placeholder='Enter password' variant="outlined" />
                                                <OutlinedInput
                                                    id="outlined-adornment-password"
                                                    type={values.showPassword ? 'text' : 'password'}
                                                    value={values.password}
                                                    onChange={handleChange('password')}
                                                    endAdornment={
                                                    <InputAdornment position="end">
                                                        <IconButton
                                                        aria-label="toggle password visibility"
                                                        onClick={handleClickShowPassword}
                                                        onMouseDown={handleMouseDownPassword}
                                                        edge="end"
                                                        >
                                                        {values.showPassword ? <VisibilityOff /> : <Visibility />}
                                                        </IconButton>
                                                    </InputAdornment>
                                                    }
                                                    label="Password"
                                                />
                                                {/* <span className='eye-btn' onClick={toggleShow}>
                                                    <img src={process.env.PUBLIC_URL + '/images/non-visibleicon.png'} alt="" class="img-fluid" />
                                                    <img src={process.env.PUBLIC_URL + '/images/visibleicon.png'} alt="" class="img-fluid" />
                                                </span> */}
                                            </div>
                                        </div>
                                        <div class="form-btn flex-between">
                                            <button type="submit" class="primary-button">Log In</button>
                                            <div class="forgot-password-txt">Forgot Password?</div>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            {/* <footer>
                <section class="footer">
                    <div class="container">
                    <div class="footer-wrapper flex-between">
                        <div class="copyright-wrapper">
                        &#169; 2022 CGF - Human Right Due Diligence (HRDD) Dashboard System
                        </div>
                        <div className='version-txt'>Version 1.0</div>
                    </div>

                    </div>
                </section>
            </footer> */}
        </div>
    )
}

export default Login
