import React from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import CheckIcon from '@mui/icons-material/Check';
import "./ErrorToaster.css"
function ErrorToaster() {
    const showToasts = () => {
        toast(customMsg, {
         position: "top-right",
         autoClose: false,
         hideProgressBar: true,
         closeOnClick: true,
         pauseOnHover: true,
         draggable: true,
         progress: undefined,
         });
         }

         const customMsg = ({ closeToast }) => (
         <div className='toaster-blk flex-between'>
            <div className='toaster-left'>
                <div className='toaster-icon'><CheckIcon/></div>
            </div>
            <div className='toaster-right'>
                <div className='toaster-ttl'>Hey that's very nice!</div>
                <p>This is a success message toaster. This the second line.</p>
            </div>
         </div>
         )
         return (
            <div className='App'>
            <button onClick={showToasts}>Show Toast !</button>
            <ToastContainer />
            </div>
         );

}

export default ErrorToaster
